import './form-input.styles.scss';

const FormIput = ({label, ...otherProps}) => {
    return(
        <div className="group">
            <input className='form-input' { ...otherProps} />
            {
            label && (
            <label  className={` ${otherProps.value.length ? 'shrink' : ''} form-input-label`} > 
            {label} 
            </label>
            )}
        </div>
    )
}

export default FormIput;
